(function() {
    'use strict';
    if (typeof window.Recharts !== 'undefined') {
        return; // Already loaded
    }
    console.warn(
        'Loomworks Dashboard: Recharts library not installed. ' +
        'Chart widgets will show placeholders. ' +
        'See loomworks_dashboard/static/src/lib/recharts/recharts.min.js for instructions.'
    );
    // Provide minimal no-op stubs for the Recharts components used by dashboard
    var NoopComponent = function(props) {
        if (typeof React !== 'undefined') {
            return React.createElement('div', {
                className: 'lw-chart-placeholder',
                style: {
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '100%',
                    color: '#6c757d',
                    fontSize: '14px',
                },
            }, 'Chart library not installed');
        }
        return null;
    };
    window.Recharts = {
        ResponsiveContainer: NoopComponent,
        LineChart: NoopComponent,
        Line: NoopComponent,
        BarChart: NoopComponent,
        Bar: NoopComponent,
        AreaChart: NoopComponent,
        Area: NoopComponent,
        PieChart: NoopComponent,
        Pie: NoopComponent,
        Cell: NoopComponent,
        XAxis: NoopComponent,
        YAxis: NoopComponent,
        CartesianGrid: NoopComponent,
        Tooltip: NoopComponent,
        Legend: NoopComponent,
    };
})();
